mapper.insert:
INSERT INTO loan_product
SET loan_prod_nm = #{loanProdNm}
,  loan_des = #{loanDes}
,  loan_max_amt = #{loanMaxAmt}
,  loan_rpmt_tp = #{loanRpmtTp}
,  loan_term_mo = #{loanTermMo}
,  loan_st_dt = #{loanStDt}
,  loan_ed_dt = #{loanEdDt}
,  loan_sts_yn = #{loanStsYn}

mapper.select:
SELECT loan_max_amt, loan_term_mo
FROM loan_product
WHERE loan_prod_id = #{loanProdId}
